from splunk.quarantine_files.configs.jquery_2_config import EnableJQuery2
from splunk.quarantine_files.configs.bad_imports_config import EnableBadImports

CONFIGS = [ EnableJQuery2, EnableBadImports ]

def get_all_configs(session_key):
    """
    Loads all quarantine configurations based on the global CONFIGS list in this file.

    @type  session_key: string
    @param session_key: A key provided by the scripted input in order to authorize requests.

    @rtype: Config[]
    @return: A list of quarantine configuration instances.
    """
    config_instances = []
    for Config in CONFIGS:
        config_instances.append(Config(session_key))
    return config_instances
